  const step =1;
  return ( <div className="steps">
    <div className="numbers">
      <div className={`${step >=1 ? 'active':''}`}>1</div>
      <div>2</div>
      <div>3</div>
      </div>

      <p className="message">Step {step}: {messages[step-1]}</p>

      <div className="buttons">
        <button style={{background:'#7950f2', color: '#fff'}}>
          Previous
        </button>
        <button style={{background:'#7950f2', color: '#fff'}}>
          Next
        </button>
      </div>
  </div>
);
}

export default App;
