import { useEffect } from "react";
import { useQuiz } from "../contexts/QuizContext";

function Timer() {
  const { dispatch, secondsRemaining,remainSecond} = useQuiz();

  let remain=remainSecond;
  const mins = Math.floor(secondsRemaining / 60);
  const seconds = secondsRemaining % 60;

  useEffect(
    function () {
      const id = setInterval(function () {
        //secondsRemaining-=1;
        //if (secondsRemaining===0){dispatch({ type: "tick" });}
        remain--;
        console.log(remain)
        //dispatch({ type: "tick" });
      }, 1000);

      return () => clearInterval(id);
    },
    [dispatch,secondsRemaining,remain]
  );

  return (
    <div className="timer">
      {mins < 10 && "0"}
      {mins}:{seconds < 10 && "0"}
      {seconds}
    </div>
  );
}

export default Timer;
